process.env.SL_ENV = process.env.SL_ENV || 'dev';
require('../').profile('deadbeef', 'deadbeef', { quiet: true });

var assert = require('assert');
var http = require('http');
var net = require('net');
var nf = require('../lib/nodefly');

http.createServer(assert.fail).listen(0, '127.0.0.1', function() {
  assert(nf.server_obj === this);
  assert.equal(nf.server_obj.connCount, 0);
  this.on('upgrade', function(req, conn, head) {
    assert.equal(nf.server_obj.connCount, 1);
    seenUpgrade = true;
    conn.destroy();
    this.close();
  });
  net.connect({
    host: this.address().address,
    port: this.address().port,
  }, function() {
    this.write('GET / HTTP/1.1\r\n' +
               'Upgrade: Yes, please.\r\n' +
               '\r\n');
  });
});

process.on('exit', function() {
  assert.equal(seenUpgrade, true);
});
var seenUpgrade = false;
